            //importClass(java.lang.System);
            importPackage(java.lang);

		function helloWorld(evt) {
			System.out.println("hello world!!");

			x = evt.getClientX();
			y = evt.getClientY();
                 currentScale = document.getRootElement().getCurrentScale();
                 currentTranslate = document.getRootElement().getCurrentTranslate();

                  x = x/currentScale;
                  y = y/currentScale;
			x -= currentTranslate.getX();
                  y -= currentTranslate.getY();
                  
			System.out.println(evt.getClientX() + ", " + evt.getClientY() + ", " + x + ", " + y);
		}

            function helloWorld2() {
			System.out.println("hello world2!!");
 		}

